export function getWalletInfo(providerType: SupportedWalletProviders): WalletInfo {
	switch (providerType) {
		case StaticProviders.MetaMask:
			return getEvmInjectedProviderInfo()
		case StaticProviders.WalletConnectV2:
			return {
				id: providerType,
				label: 'WalletConnect',
				icon: walletConnectV2SVG,
			}
		case StaticProviders.Torus:
			return {
				id: providerType,
				label: 'Torus',
				icon: torusSVG,
			}
		case StaticProviders.CoinbaseSmartWallet:
			return {
				id: providerType,
				label: 'CoinBase',
				icon: smartWalletSVG,
			};
	}
}

function getEvmInjectedProviderInfo(): WalletInfo {
	const browser = getBrowserData()

	let injectedWallet = WALLET_LIST.ethereumWallet

	if (browser.brave) injectedWallet = WALLET_LIST.braveWallet
	if (browser.anyMetamask) injectedWallet = WALLET_LIST.metaMask
	if (browser.mew) injectedWallet = WALLET_LIST.mew
	if (browser.trust) injectedWallet = WALLET_LIST.trustWallet
	if (browser.status) injectedWallet = WALLET_LIST.statusWallet
	if (browser.alphaWallet) injectedWallet = WALLET_LIST.alphaWallet
	if (browser.imToken) injectedWallet = WALLET_LIST.imToken
	if (browser.okx) injectedWallet = WALLET_LIST.okx
	if (browser.kaikas) injectedWallet = WALLET_LIST.kaikas;
	if (browser.oneKey) injectedWallet = WALLET_LIST.onekey;
	if (browser.tokenPocket) injectedWallet = WALLET_LIST.tokenPocket;
	if (browser.foxWallet) injectedWallet = WALLET_LIST.foxWallet;
	if (browser.coin98Wallet) injectedWallet = WALLET_LIST.coin98Wallet;
	if (browser.gateWallet) injectedWallet = WALLET_LIST.gateWallet;

	return {
		id: StaticProviders.MetaMask,
		...injectedWallet,
	}
}
